        {props.breed &&
          props.breed.map((item, index) => (
            <li key={index}>
              <a href={item.href}>{item.name}</a>
              {props.breedIcon}
            </li>
          ))}
      </ul>
    </div>
  );
}; */

export { Modal, MaterialInput, MaterialButton, DropdownMenu, Anchor/* , Breed  */};
